    83‚Üí                        LabeledContent("Est. Duration", value: "~\(viewModel.estimatedDuration) min")
    84‚Üí                    }
    85‚Üí                }
    86‚Üí            }
    87‚Üí            .navigationTitle(viewModel.isEditing ? "Edit Workout" : "New Workout")
    88‚Üí            .navigationBarTitleDisplayMode(.inline)
    89‚Üí            .toolbar {
