   113‚Üí                }
   114‚Üí            }
   115‚Üí            .sheet(isPresented: $viewModel.showExercisePicker) {
   116‚Üí                ExercisePickerView(exerciseService: viewModel.exerciseService) { exercise in
   117‚Üí                    viewModel.addExercise(from: exercise, imageURL: nil)
   118‚Üí                }
   119‚Üí            }
