   125‚Üí            } message: {
   126‚Üí                Text(viewModel.error?.localizedDescription ?? "An error occurred")
   127‚Üí            }
   128‚Üí            .overlay {
   129‚Üí                if viewModel.isLoading {
   130‚Üí                    ProgressView()
   131‚Üí                        .scaleEffect(1.5)
   132‚Üí                        .frame(maxWidth: .infinity, maxHeight: .infinity)
   133‚Üí                        .background(.ultraThinMaterial)
   134‚Üí                }
   135‚Üí            }
   136‚Üí        }
   137‚Üí    }
