   313‚Üí// MARK: - ViewModel
   314‚Üí
   315‚Üí@MainActor
   316‚Üí@Observable
   317‚Üífinal class EditWorkoutViewModel {
   318‚Üí    var workoutName = ""
   319‚Üí    private(set) var exercises: [CustomExerciseEntry] = []
   320‚Üí    private(set) var isLoading = false
   321‚Üí    var errorMessage: String?
   322‚Üí
   323‚Üí    private let resolver: Resolver
   324‚Üí    private let workoutId: UUID
   325‚Üí    private var originalWorkout: CustomWorkoutTemplate?
   326‚Üí
   327‚Üí    var canSave: Bool {
   328‚Üí        !workoutName.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty &&
   329‚Üí        !exercises.isEmpty &&
   330‚Üí        !isLoading
   331‚Üí    }
   332‚Üí
   333‚Üí    init(resolver: Resolver, workoutId: UUID) {
   334‚Üí        self.resolver = resolver
   335‚Üí        self.workoutId = workoutId
   336‚Üí    }
   337‚Üí
   338‚Üí    func loadWorkout() async {
   339‚Üí        isLoading = true
   340‚Üí        errorMessage = nil
   341‚Üí
   342‚Üí        defer { isLoading = false }
   343‚Üí
   344‚Üí        guard let repository = resolver.resolve(CustomWorkoutRepository.self) else {
   345‚Üí            errorMessage = "Reposit√≥rio n√£o dispon√≠vel"
   346‚Üí            return
   347‚Üí        }
   348‚Üí
   349‚Üí        do {
   350‚Üí            guard let workout = try await repository.getTemplate(id: workoutId) else {
   351‚Üí                errorMessage = "Treino n√£o encontrado"
   352‚Üí                return
   353‚Üí            }
   354‚Üí
   355‚Üí            originalWorkout = workout
   356‚Üí            workoutName = workout.name
   357‚Üí            exercises = workout.exercises
   358‚Üí
   359‚Üí        } catch {
   360‚Üí            errorMessage = "Erro ao carregar treino: \(error.localizedDescription)"
   361‚Üí        }
   362‚Üí    }
   363‚Üí
   364‚Üí    func addExerciseEntry(_ entry: CustomExerciseEntry) {
   365‚Üí        // Adicionar entrada j√° convertida pelo ExerciseSearchSheet
