//
//  EditWorkoutView.swift
//  FitToday
//
//  View for editing an existing custom workout template.
//

import SwiftUI
import Swinject

/// View for editing an existing custom workout template.
struct EditWorkoutView: View {
    let resolver: Resolver
    let workoutId: UUID
    let onDismiss: () -> Void

    @Environment(\.dismiss) private var dismiss
    @State private var viewModel: EditWorkoutViewModel?
    @State private var showExerciseSearch = false
    @State private var isEditMode = false
    @State private var showDeleteConfirmation = false

    var body: some View {
        NavigationStack {
            Group {
                if let viewModel = viewModel {
                    if viewModel.isLoading {
                        loadingView
                    } else if viewModel.errorMessage != nil {
                        errorView
                    } else {
                        contentView(viewModel: viewModel)
                    }
                } else {
                    loadingView
                }
            }
            .background(FitTodayColor.background)
            .navigationTitle("Editar Treino")
            .navigationBarTitleDisplayMode(.inline)
            .toolbar {
                ToolbarItem(placement: .cancellationAction) {
                    Button("Cancelar") {
                        onDismiss()
                        dismiss()
                    }
                    .foregroundStyle(FitTodayColor.textSecondary)
                }

                if let viewModel = viewModel, !viewModel.exercises.isEmpty {
                    ToolbarItem(placement: .principal) {
                        Button {
                            withAnimation(.easeInOut(duration: 0.2)) {
                                isEditMode.toggle()
                            }
                        } label: {
                            Text(isEditMode ? "Conclu√≠do" : "Reordenar")
                                .font(FitTodayFont.ui(size: 14, weight: .medium))
                                .foregroundStyle(FitTodayColor.brandPrimary)
                        }
                    }
                }

                ToolbarItem(placement: .confirmationAction) {
                    Button("Salvar") {
                        Task {
                            await viewModel?.saveChanges()
                            onDismiss()
                            dismiss()
                        }
                    }
                    .foregroundStyle(viewModel?.canSave == true ? FitTodayColor.brandPrimary : FitTodayColor.textTertiary)
                    .disabled(viewModel?.canSave != true)
                }
            }
            .task {
                let vm = EditWorkoutViewModel(resolver: resolver, workoutId: workoutId)
                viewModel = vm
                await vm.loadWorkout()
            }
            .sheet(isPresented: $showExerciseSearch) {
                if let exerciseService = resolver.resolve(ExerciseServiceProtocol.self) {
                    ExerciseSearchSheet(
                        exerciseService: exerciseService,
                        onSelect: { exerciseEntry in
                            viewModel?.addExerciseEntry(exerciseEntry)
                        }
                    )
                }
            }
            .confirmationDialog(
                "Excluir Treino",
                isPresented: $showDeleteConfirmation,
                titleVisibility: .visible
            ) {
                Button("Excluir", role: .destructive) {
                    Task {
                        await viewModel?.deleteWorkout()
                        onDismiss()
                        dismiss()
                    }
                }
                Button("Cancelar", role: .cancel) {}
            } message: {
                Text("Tem certeza que deseja excluir este treino? Esta a√ß√£o n√£o pode ser desfeita.")
            }
        }
    }

    // MARK: - Loading View

    private var loadingView: some View {
        VStack(spacing: FitTodaySpacing.md) {
            ProgressView()
                .tint(FitTodayColor.brandPrimary)
            Text("Carregando treino...")
                .font(FitTodayFont.ui(size: 14, weight: .medium))
                .foregroundStyle(FitTodayColor.textSecondary)
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
    }

    // MARK: - Error View

    private var errorView: some View {
        VStack(spacing: FitTodaySpacing.md) {
            Image(systemName: "exclamationmark.triangle")
                .font(.system(size: 48))
                .foregroundStyle(FitTodayColor.warning)

            Text("Erro ao carregar treino")
                .font(FitTodayFont.ui(size: 17, weight: .semiBold))
                .foregroundStyle(FitTodayColor.textPrimary)

            if let error = viewModel?.errorMessage {
                Text(error)
                    .font(FitTodayFont.ui(size: 14, weight: .medium))
                    .foregroundStyle(FitTodayColor.textSecondary)
                    .multilineTextAlignment(.center)
            }
        }
        .frame(maxWidth: .infinity, maxHeight: .infinity)
        .padding(FitTodaySpacing.lg)
    }

    // MARK: - Content View

    @ViewBuilder
    private func contentView(viewModel: EditWorkoutViewModel) -> some View {
        ScrollView {
            VStack(spacing: FitTodaySpacing.lg) {
                // Workout Name
                nameSection(viewModel: viewModel)

                // Exercises List
                exercisesSection(viewModel: viewModel)

                // Add Exercise Button
                addExerciseButton

                // Delete Workout Button
                deleteWorkoutButton

                Spacer(minLength: FitTodaySpacing.xxl)
            }
            .padding(FitTodaySpacing.md)
        }
        .scrollIndicators(.hidden)
    }

    // MARK: - Name Section

    private func nameSection(viewModel: EditWorkoutViewModel) -> some View {
        VStack(alignment: .leading, spacing: FitTodaySpacing.sm) {
            Text("Nome do treino")
                .font(FitTodayFont.ui(size: 14, weight: .semiBold))
                .foregroundStyle(FitTodayColor.textSecondary)

            TextField("Ex: Treino A - Peito e Tr√≠ceps", text: Binding(
                get: { viewModel.workoutName },
                set: { viewModel.workoutName = $0 }
            ))
                .font(FitTodayFont.ui(size: 17, weight: .medium))
                .foregroundStyle(FitTodayColor.textPrimary)
                .padding(FitTodaySpacing.md)
                .background(
                    RoundedRectangle(cornerRadius: FitTodayRadius.sm)
                        .fill(FitTodayColor.surface)
                        .overlay(
                            RoundedRectangle(cornerRadius: FitTodayRadius.sm)
                                .stroke(FitTodayColor.outline, lineWidth: 1)
                        )
                )
        }
    }

    // MARK: - Exercises Section

    private func exercisesSection(viewModel: EditWorkoutViewModel) -> some View {
        VStack(alignment: .leading, spacing: FitTodaySpacing.sm) {
            HStack {
                Text("Exerc√≠cios")
                    .font(FitTodayFont.ui(size: 14, weight: .semiBold))
                    .foregroundStyle(FitTodayColor.textSecondary)

                Spacer()

                if !viewModel.exercises.isEmpty {
                    Text("\(viewModel.exercises.count)")
                        .font(FitTodayFont.ui(size: 13, weight: .medium))
                        .foregroundStyle(FitTodayColor.textTertiary)
                }
            }

            if viewModel.exercises.isEmpty {
                emptyExercisesView
            } else {
                exercisesList(viewModel: viewModel)
            }
        }
    }

    private var emptyExercisesView: some View {
        VStack(spacing: FitTodaySpacing.md) {
            Image(systemName: "plus.circle.dashed")
                .font(.system(size: 40))
                .foregroundStyle(FitTodayColor.textTertiary)

            Text("Nenhum exerc√≠cio adicionado")
                .font(FitTodayFont.ui(size: 15, weight: .medium))
                .foregroundStyle(FitTodayColor.textSecondary)
        }
        .frame(maxWidth: .infinity)
        .padding(.vertical, FitTodaySpacing.xl)
        .background(
            RoundedRectangle(cornerRadius: FitTodayRadius.md)
                .fill(FitTodayColor.surface)
                .overlay(
                    RoundedRectangle(cornerRadius: FitTodayRadius.md)
                        .strokeBorder(style: StrokeStyle(lineWidth: 1, dash: [8]))
                        .foregroundStyle(FitTodayColor.outline)
                )
        )
    }

    private func exercisesList(viewModel: EditWorkoutViewModel) -> some View {
        VStack(spacing: FitTodaySpacing.sm) {
            ForEach(Array(viewModel.exercises.enumerated()), id: \.element.id) { index, exercise in
                ExerciseEntryRow(
                    exercise: exercise,
                    index: index + 1,
                    isEditMode: isEditMode,
                    onDelete: {
                        withAnimation(.easeInOut(duration: 0.2)) {
                            viewModel.removeExercise(exercise)
                        }
                    },
                    onMoveUp: index > 0 ? {
                        withAnimation(.easeInOut(duration: 0.2)) {
                            viewModel.moveExercise(from: IndexSet(integer: index), to: index - 1)
                        }
                    } : nil,
                    onMoveDown: index < viewModel.exercises.count - 1 ? {
                        withAnimation(.easeInOut(duration: 0.2)) {
                            viewModel.moveExercise(from: IndexSet(integer: index), to: index + 2)
                        }
                    } : nil,
                    onConfigureSets: {
                        // TODO: Open sets configuration sheet
                    }
                )
            }
        }
    }

    // MARK: - Add Exercise Button

    private var addExerciseButton: some View {
        Button {
            showExerciseSearch = true
        } label: {
            HStack(spacing: FitTodaySpacing.sm) {
                Image(systemName: "plus.circle.fill")
                Text("Adicionar Exerc√≠cio")
            }
        }
        .fitSecondaryStyle()
    }

    // MARK: - Delete Workout Button

    private var deleteWorkoutButton: some View {
        Button {
            showDeleteConfirmation = true
        } label: {
            HStack(spacing: FitTodaySpacing.sm) {
                Image(systemName: "trash")
                Text("Excluir Treino")
            }
            .font(FitTodayFont.ui(size: 15, weight: .medium))
            .foregroundStyle(FitTodayColor.error)
            .frame(maxWidth: .infinity)
            .padding(FitTodaySpacing.md)
            .background(
                RoundedRectangle(cornerRadius: FitTodayRadius.md)
                    .fill(FitTodayColor.error.opacity(0.1))
            )
        }
        .buttonStyle(.plain)
    }
}

// MARK: - ViewModel

@MainActor
@Observable
final class EditWorkoutViewModel {
    var workoutName = ""
    private(set) var exercises: [CustomExerciseEntry] = []
    private(set) var isLoading = false
    var errorMessage: String?

    private let resolver: Resolver
    private let workoutId: UUID
    private var originalWorkout: CustomWorkoutTemplate?

    var canSave: Bool {
        !workoutName.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty &&
        !exercises.isEmpty &&
        !isLoading
    }

    init(resolver: Resolver, workoutId: UUID) {
        self.resolver = resolver
        self.workoutId = workoutId
    }

    func loadWorkout() async {
        isLoading = true
        errorMessage = nil

        defer { isLoading = false }

        guard let repository = resolver.resolve(CustomWorkoutRepository.self) else {
            errorMessage = "Reposit√≥rio n√£o dispon√≠vel"
            return
        }

        do {
            guard let workout = try await repository.getTemplate(id: workoutId) else {
                errorMessage = "Treino n√£o encontrado"
                return
            }

            originalWorkout = workout
            workoutName = workout.name
            exercises = workout.exercises

        } catch {
            errorMessage = "Erro ao carregar treino: \(error.localizedDescription)"
        }
    }

    func addExerciseEntry(_ entry: CustomExerciseEntry) {
        // Adicionar entrada j√° convertida pelo ExerciseSearchSheet
        var newEntry = entry
        newEntry.orderIndex = exercises.count
        exercises.append(newEntry)
    }

    func removeExercise(_ exercise: CustomExerciseEntry) {
        exercises.removeAll { $0.id == exercise.id }
    }

    func moveExercise(from source: IndexSet, to destination: Int) {
        exercises.move(fromOffsets: source, toOffset: destination)
    }

    func saveChanges() async {
        isLoading = true
        errorMessage = nil

        defer { isLoading = false }

        guard let saveUseCase = resolver.resolve(SaveCustomWorkoutUseCase.self) else {
            errorMessage = "Servi√ßo de salvamento n√£o dispon√≠vel"
            return
        }

        do {
            // Create updated template preserving original ID
            let updatedTemplate = CustomWorkoutTemplate(
                id: workoutId,
                name: workoutName.trimmingCharacters(in: .whitespacesAndNewlines),
                exercises: exercises,
                createdAt: originalWorkout?.createdAt ?? Date(),
                lastUsedAt: originalWorkout?.lastUsedAt
            )

            try await saveUseCase.execute(template: updatedTemplate)

            #if DEBUG
            print("[EditWorkout] Workout saved successfully: \(workoutName)")
            #endif

        } catch {
            errorMessage = "Erro ao salvar: \(error.localizedDescription)"
        }
    }

    func deleteWorkout() async {
        isLoading = true
        errorMessage = nil

        defer { isLoading = false }

        guard let repository = resolver.resolve(CustomWorkoutRepository.self) else {
            errorMessage = "Reposit√≥rio n√£o dispon√≠vel"
            return
        }

        do {
            try await repository.deleteTemplate(id: workoutId)

            #if DEBUG
            print("[EditWorkout] Workout deleted: \(workoutId)")
            #endif

        } catch {
            errorMessage = "Erro ao excluir: \(error.localizedDescription)"
        }
    }
}

// MARK: - Preview

#Preview {
    let container = Container()
    return EditWorkoutView(resolver: container, workoutId: UUID()) {}
        .preferredColorScheme(.dark)
}

//
//  WorkoutPreviewView.swift
//  FitToday
//
//  Preview screen showing exercises before starting a workout.
//  User can review the workout structure, then tap "Iniciar Treino" to begin execution.
//

import SwiftUI
import Swinject

struct WorkoutPreviewView: View {
    let workout: ProgramWorkout
    let resolver: Resolver

    @Environment(AppRouter.self) private var router
    @Environment(WorkoutSessionStore.self) private var sessionStore
    @State private var showStartConfirmation = false

    var body: some View {
        ScrollView {
            VStack(spacing: FitTodaySpacing.lg) {
                headerSection
                exercisesPreviewSection
                startButton
            }
            .padding()
        }
        .background(FitTodayColor.background.ignoresSafeArea())
        .navigationTitle("Pr√©-visualiza√ß√£o")
        .navigationBarTitleDisplayMode(.inline)
        .toolbar(.hidden, for: .tabBar)
        .confirmationDialog("Iniciar Treino?", isPresented: $showStartConfirmation) {
            Button("Iniciar Treino") {
                startWorkout()
            }
            Button("Cancelar", role: .cancel) {}
        } message: {
            Text("Voc√™ est√° pronto para come√ßar este treino de \(workout.estimatedDurationMinutes) minutos?")
        }
    }

    // MARK: - Header Section

    private var headerSection: some View {
        VStack(alignment: .leading, spacing: FitTodaySpacing.md) {
            Text(workout.title)
                .font(.system(.title2, weight: .bold))
                .foregroundStyle(FitTodayColor.textPrimary)

            Text(workout.subtitle)
                .font(.system(.subheadline))
                .foregroundStyle(FitTodayColor.textSecondary)

            // Stats row
            HStack(spacing: FitTodaySpacing.lg) {
                StatBadge(
                    icon: "clock",
                    value: "\(workout.estimatedDurationMinutes)",
                    label: "min"
                )

                StatBadge(
                    icon: "figure.run",
                    value: "\(workout.exercises.count)",
                    label: "exerc√≠cios"
                )

                StatBadge(
                    icon: "flame",
                    value: "\(estimatedCalories)",
                    label: "kcal"
                )
            }
        }
        .padding(FitTodaySpacing.lg)
        .frame(maxWidth: .infinity, alignment: .leading)
        .background(FitTodayColor.surface)
        .clipShape(RoundedRectangle(cornerRadius: FitTodayRadius.md))
    }

    private var estimatedCalories: Int {
        // Rough estimate: 5 kcal per minute of exercise
        workout.estimatedDurationMinutes * 5
    }

    // MARK: - Exercises Preview Section

    private var exercisesPreviewSection: some View {
        VStack(alignment: .leading, spacing: FitTodaySpacing.md) {
            Text("Exerc√≠cios do Treino")
                .font(.system(.headline, weight: .semibold))
                .foregroundStyle(FitTodayColor.textPrimary)

            if workout.exercises.isEmpty {
                emptyExercisesView
            } else {
                LazyVStack(spacing: FitTodaySpacing.sm) {
                    ForEach(Array(workout.exercises.enumerated()), id: \.element.id) { index, exercise in
                        ExercisePreviewCard(
                            exercise: exercise,
                            index: index + 1
                        )
                    }
                }
            }
        }
    }

    private var emptyExercisesView: some View {
        VStack(spacing: FitTodaySpacing.md) {
            Image(systemName: "exclamationmark.circle")
                .font(.system(size: 40))
                .foregroundStyle(FitTodayColor.textTertiary)

            Text("Nenhum exerc√≠cio encontrado")
                .font(.system(.subheadline))
                .foregroundStyle(FitTodayColor.textSecondary)
        }
        .frame(maxWidth: .infinity)
        .padding(FitTodaySpacing.xl)
    }

    // MARK: - Start Button

    private var startButton: some View {
        Button {
            showStartConfirmation = true
        } label: {
            HStack(spacing: FitTodaySpacing.sm) {
                Image(systemName: "play.fill")
                Text("Iniciar Treino")
                    .font(.system(.headline, weight: .semibold))
            }
            .foregroundStyle(.white)
            .frame(maxWidth: .infinity)
            .padding()
            .background(FitTodayColor.brandPrimary)
            .clipShape(RoundedRectangle(cornerRadius: FitTodayRadius.md))
        }
        .disabled(workout.exercises.isEmpty)
    }

    // MARK: - Actions

    private func startWorkout() {
        // Convert ProgramWorkout to WorkoutPlan for execution
        let workoutPlan = convertToWorkoutPlan()

        // Initialize workout session
        sessionStore.start(with: workoutPlan)

        // Navigate to workout execution view
        router.push(.workoutExecution, on: router.selectedTab)
    }

    // MARK: - Conversion Helper

    /// Converts ProgramWorkout to WorkoutPlan for execution
    private func convertToWorkoutPlan() -> WorkoutPlan {
        let exercisePrescriptions = workout.exercises.map { programExercise -> ExercisePrescription in
            let wgerExercise = programExercise.wgerExercise
            let workoutExercise = WorkoutExercise(
                id: String(wgerExercise.id),
                name: wgerExercise.name,
                mainMuscle: mapCategoryToMuscleGroup(wgerExercise.category),
                equipment: mapEquipment(wgerExercise.equipment),
                instructions: extractInstructions(from: wgerExercise),
                media: createMedia(from: wgerExercise)
            )

            return ExercisePrescription(
                exercise: workoutExercise,
                sets: programExercise.sets,
                reps: IntRange(
                    programExercise.repsRange.lowerBound,
                    programExercise.repsRange.upperBound
                ),
                restInterval: TimeInterval(programExercise.restSeconds),
                tip: programExercise.notes
            )
        }

        return WorkoutPlan(
            id: UUID(),
            title: workout.title,
            focus: .fullBody,
            estimatedDurationMinutes: workout.estimatedDurationMinutes,
            intensity: .moderate,
            exercises: exercisePrescriptions,
            createdAt: Date()
        )
    }

    // MARK: - Mapping Helpers

    private func mapCategoryToMuscleGroup(_ categoryId: Int?) -> MuscleGroup {
        guard let categoryId else { return .fullBody }

        // Map Wger category IDs to MuscleGroup
        switch categoryId {
        case 8:  return .arms          // Arms
        case 9:  return .quads         // Legs
        case 10: return .core          // Abs
        case 11: return .chest         // Chest
        case 12: return .back          // Back
        case 13: return .shoulders     // Shoulders
        case 14: return .calves        // Calves
        case 15: return .cardioSystem  // Cardio
        default: return .fullBody
        }
    }

    private func mapEquipment(_ equipmentIds: [Int]) -> EquipmentType {
        guard let first = equipmentIds.first else { return .bodyweight }

        // Map Wger equipment IDs to EquipmentType
        switch first {
        case 1: return .barbell
        case 3: return .dumbbell
        case 8: return .machine
        case 10: return .kettlebell
        case 7: return .bodyweight
        case 9: return .resistanceBand
        case 6: return .pullupBar
        default: return .bodyweight
        }
    }

    private func extractInstructions(from wgerExercise: WgerExercise) -> [String] {
        guard let description = wgerExercise.description, !description.isEmpty else {
            return ["Realize o exerc√≠cio com boa t√©cnica."]
        }

        let lines = description
            .components(separatedBy: CharacterSet.newlines)
            .map { $0.trimmingCharacters(in: .whitespaces) }
            .filter { !$0.isEmpty }

        return lines.isEmpty ? ["Realize o exerc√≠cio com boa t√©cnica."] : lines
    }

    private func createMedia(from wgerExercise: WgerExercise) -> ExerciseMedia? {
        let imageURL = wgerExercise.mainImageURL.flatMap { URL(string: $0) }
            ?? wgerExercise.imageURLs.first.flatMap { URL(string: $0) }

        guard imageURL != nil else { return nil }
        return ExerciseMedia(videoURL: nil, imageURL: imageURL, gifURL: nil)
    }
}

// MARK: - Stat Badge

private struct StatBadge: View {
    let icon: String
    let value: String
    let label: String

    var body: some View {
        VStack(spacing: FitTodaySpacing.xs) {
            HStack(spacing: FitTodaySpacing.xs) {
                Image(systemName: icon)
                    .font(.system(.caption))
                Text(value)
                    .font(.system(.headline, weight: .bold))
            }
            .foregroundStyle(FitTodayColor.brandPrimary)

            Text(label)
                .font(.system(.caption))
                .foregroundStyle(FitTodayColor.textTertiary)
        }
    }
}

// MARK: - Exercise Preview Card

private struct ExercisePreviewCard: View {
    let exercise: ProgramExercise
    let index: Int

    var body: some View {
        HStack(spacing: FitTodaySpacing.md) {
            // Exercise number badge
            Text("\(index)")
                .font(.system(.subheadline, weight: .bold))
                .foregroundStyle(FitTodayColor.brandPrimary)
                .frame(width: 28, height: 28)
                .background(FitTodayColor.brandPrimary.opacity(0.15))
                .clipShape(Circle())

            // Exercise image
            exerciseImage

            // Exercise info
            VStack(alignment: .leading, spacing: FitTodaySpacing.xs) {
                Text(exercise.name)
                    .font(.system(.subheadline, weight: .semibold))
                    .foregroundStyle(FitTodayColor.textPrimary)
                    .lineLimit(2)

                // Sets x Reps info
                HStack(spacing: FitTodaySpacing.sm) {
                    Text("\(exercise.sets) s√©ries")
                    Text("‚Ä¢")
                    Text("\(exercise.repsRange.lowerBound)-\(exercise.repsRange.upperBound) reps")
                }
                .font(.system(.caption))
                .foregroundStyle(FitTodayColor.textSecondary)
            }

            Spacer()
        }
        .padding(FitTodaySpacing.md)
        .background(FitTodayColor.surface)
        .clipShape(RoundedRectangle(cornerRadius: FitTodayRadius.md))
    }

    @ViewBuilder
    private var exerciseImage: some View {
        if let imageURL = exercise.imageURL {
            ExerciseMediaImageURL(
                url: imageURL,
                size: CGSize(width: 56, height: 56),
                contentMode: .fill,
                cornerRadius: FitTodayRadius.sm
            )
        } else {
            placeholderImage
        }
    }

    private var placeholderImage: some View {
        ZStack {
            RoundedRectangle(cornerRadius: FitTodayRadius.sm)
                .fill(FitTodayColor.brandPrimary.opacity(0.1))
            Image(systemName: "figure.strengthtraining.traditional")
                .font(.system(size: 24))
                .foregroundStyle(FitTodayColor.brandPrimary)
        }
        .frame(width: 56, height: 56)
    }
}

