   246‚Üí    private func prefetchWorkoutImages() async {
   247‚Üí        guard let plan = sessionStore.plan,
   248‚Üí              let cacheService = imageCacheService else {
   249‚Üí            return
   250‚Üí        }
   251‚Üí
   252‚Üí        // OPTIMIZATION: Only prefetch first 3 images to save API requests
   253‚Üí        // Remaining images load on-demand when user views each exercise
   254‚Üí        let urls = Array(plan.imageURLs.prefix(3))
   255‚Üí        guard !urls.isEmpty else { return }
   256‚Üí
