   258‚Üí        await cacheService.prefetchImages(urls)
   259‚Üí        try? await Task.sleep(nanoseconds: 300_000_000) // 0.3s
   260‚Üí        isPrefetchingImages = false
   261‚Üí    }
   262‚Üí
   263‚Üí    // MARK: - Workout Regeneration
   264‚Üí
   265‚Üí    private func regenerateWorkoutPlan() {
   266‚Üí        guard !isRegenerating else { return }
   267‚Üí
   268‚Üí        isRegenerating = true
   269‚Üí
   270‚Üí        Task {
   271‚Üí            do {
   272‚Üí                let newPlan = try await generateNewPlan()
   273‚Üí
   274‚Üí                await MainActor.run {
   275‚Üí                    withAnimation(.spring(response: 0.5, dampingFraction: 0.8)) {
   276‚Üí                        sessionStore.start(with: newPlan)
   277‚Üí                        animationTrigger.toggle()
   278‚Üí                    }
   279‚Üí                }
   280‚Üí            } catch {
   281‚Üí                await MainActor.run {
   282‚Üí                    errorMessage = "N√£o foi poss√≠vel regenerar o treino: \(error.localizedDescription)"
   283‚Üí                }
   284‚Üí            }
   285‚Üí
   286‚Üí            await MainActor.run {
   287‚Üí                isRegenerating = false
   288‚Üí            }
   289‚Üí        }
   290‚Üí    }
   291‚Üí
   292‚Üí    private func generateNewPlan() async throws -> WorkoutPlan {
   293‚Üí        guard let profileRepo = resolver.resolve(UserProfileRepository.self) else {
   294‚Üí            throw DomainError.repositoryFailure(reason: "Reposit√≥rio de perfil n√£o dispon√≠vel.")
   295‚Üí        }
   296‚Üí
   297‚Üí        guard let profile = try await profileRepo.loadProfile() else {
   298‚Üí            throw DomainError.profileNotFound
   299‚Üí        }
   300‚Üí
   301‚Üí        guard let checkInData = UserDefaults.standard.data(forKey: AppStorageKeys.lastDailyCheckInData),
   302‚Üí              let checkIn = try? JSONDecoder().decode(DailyCheckIn.self, from: checkInData) else {
   303‚Üí            throw DomainError.invalidInput(reason: "Responda o question√°rio di√°rio primeiro.")
   304‚Üí        }
   305‚Üí
   306‚Üí        guard let blocksRepo = resolver.resolve(WorkoutBlocksRepository.self) else {
   307‚Üí            throw DomainError.repositoryFailure(reason: "Reposit√≥rio de blocos n√£o dispon√≠vel.")
   308‚Üí        }
   309‚Üí
   310‚Üí        let blocks = try await blocksRepo.loadBlocks()
   311‚Üí
   312‚Üí        if isPro {
   313‚Üí            guard let composer = resolver.resolve(WorkoutPlanComposing.self) else {
   314‚Üí                throw DomainError.repositoryFailure(reason: "Compositor n√£o dispon√≠vel.")
   315‚Üí            }
   316‚Üí            return try await composer.composePlan(blocks: blocks, profile: profile, checkIn: checkIn)
   317‚Üí        } else {
   318‚Üí            let localComposer = LocalWorkoutPlanComposer()
   319‚Üí            return try await localComposer.composePlan(blocks: blocks, profile: profile, checkIn: checkIn)
   320‚Üí        }
   321‚Üí    }
