import Foundation

/// Represents a single set within a custom exercise entry.
/// Can be used for both target (planned) and actual (performed) tracking.
struct WorkoutSet: Identifiable, Codable, Sendable, Hashable {
    let id: UUID

    // Target values (what user plans to do)
    var targetReps: Int?
    var targetWeight: Double?
    var targetDuration: TimeInterval? // For timed exercises (planks, etc.)

    // Actual values (what user actually did)
    var actualReps: Int?
    var actualWeight: Double?
    var actualDuration: TimeInterval?

    // Completion state
    var isCompleted: Bool

    /// Creates a new set with default values
    init(
        id: UUID = UUID(),
        targetReps: Int? = 10,
        targetWeight: Double? = nil,
        targetDuration: TimeInterval? = nil,
        actualReps: Int? = nil,
        actualWeight: Double? = nil,
        actualDuration: TimeInterval? = nil,
        isCompleted: Bool = false
    ) {
        self.id = id
        self.targetReps = targetReps

/// ViewModel for creating and editing custom workout templates.
@Observable
@MainActor
final class CreateWorkoutViewModel {
    // MARK: - Properties

    var workoutName: String = ""
    var selectedCategory: String?
    var selectedIcon: WorkoutIcon = .dumbbell
    var selectedColor: WorkoutColor = .blue
    var exercises: [CustomExerciseEntry] = []
    var notes: String = ""

    var isSaving = false
    var errorMessage: String?

    private let saveUseCase: SaveCustomWorkoutUseCase?

    init(saveUseCase: SaveCustomWorkoutUseCase? = nil) {
        self.saveUseCase = saveUseCase
    }

    // MARK: - Static Data

    static let categories = [
        "Push",
        "Pull",
        "Legs",
        "Upper",
        "Lower",
        "Full Body",
        "Core",
        "Cardio"
    ]

    // MARK: - Computed Properties

    var canSave: Bool {
        !workoutName.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty &&
        !exercises.isEmpty
    }

    var totalSets: Int {
        exercises.reduce(0) { $0 + $1.sets.count }
    }

    var estimatedDuration: Int {
        exercises.reduce(0) { $0 + $1.estimatedDurationMinutes }
    }

func removeExercise(_ exercise: CustomExerciseEntry) {
    exercises.removeAll { $0.id == exercise.id }
    reorderExercises()
}

func moveExercise(from source: IndexSet, to destination: Int) {
    exercises.move(fromOffsets: source, toOffset: destination)
    reorderExercises()
}

func saveWorkout() async {
    guard canSave, let saveUseCase else { return }

    isSaving = true
    errorMessage = nil

    do {
        let template = CustomWorkoutTemplate(
            name: workoutName.trimmingCharacters(in: .whitespacesAndNewlines),
            exercises: exercises,
            workoutDescription: notes.isEmpty ? nil : notes,
            category: selectedCategory
        )

        try await saveUseCase.execute(template: template)

        #if DEBUG
        print("[CreateWorkout] Saved template: \(template.name) with \(template.exerciseCount) exercises")
        #endif
    } catch {
        errorMessage = error.localizedDescription
    }

    isSaving = false
}

func reset() {
    workoutName = ""
    selectedCategory = nil
    exercises = []

