   318‚Üí    // MARK: - Live Activity Management
   319‚Üí
   320‚Üí    /// Starts periodic timer to update Live Activity
   321‚Üí    private func startLiveActivityUpdateTimer() {
   322‚Üí        // Cancel any existing timer
   323‚Üí        liveActivityUpdateTimer?.invalidate()
   324‚Üí
   325‚Üí        // Create new timer that fires every second
   326‚Üí        liveActivityUpdateTimer = Timer.scheduledTimer(withTimeInterval: 1.0, repeats: true) { [weak self] _ in
   327‚Üí            guard let self else { return }
   328‚Üí            Task { @MainActor in
   329‚Üí                await self.updateLiveActivity()
   330‚Üí            }
   331‚Üí        }
   332‚Üí    }
   333‚Üí
   334‚Üí    /// Stops the Live Activity update timer
   335‚Üí    private func stopLiveActivityUpdateTimer() {
   336‚Üí        liveActivityUpdateTimer?.invalidate()
   337‚Üí        liveActivityUpdateTimer = nil
   338‚Üí    }
   339‚Üí
   340‚Üí    /// Updates Live Activity with current workout state
   341‚Üí    private func updateLiveActivity() async {
   342‚Üí        guard #available(iOS 16.1, *),
   343‚Üí              let manager = liveActivityManager else {
   344‚Üí            return
   345‚Üí        }
   346‚Üí
   347‚Üí        // Determine workout state
   348‚Üí        let workoutState: WorkoutActivityAttributes.ContentState.WorkoutState
   349‚Üí        if isPaused {
   350‚Üí            workoutState = .paused
   351‚Üí        } else if isResting {
   352‚Üí            workoutState = .resting
   353‚Üí        } else {
   354‚Üí            workoutState = .active
   355‚Üí        }
   356‚Üí
   357‚Üí        // Format series information
   358‚Üí        let seriesInfo = "\(currentSetNumber)/\(totalSets)"
   359‚Üí
   360‚Üí        // Get rest timer seconds if active
   361‚Üí        let restSeconds = isResting ? Int(restTimeRemaining) : nil
   362‚Üí
   363‚Üí        // Check if rest timer just completed (FR-007 - Haptic + Sound)
   364‚Üí        if let lastSeconds = lastRestTimerSeconds,
   365‚Üí           lastSeconds > 0,
   366‚Üí           restSeconds == 0 {
   367‚Üí            // Play haptic feedback
   368‚Üí            hapticGenerator.notificationOccurred(.success)
   369‚Üí
   370‚Üí            // Play system sound (notification sound)
   371‚Üí            AudioServicesPlaySystemSound(1026)
   372‚Üí        }
   373‚Üí        lastRestTimerSeconds = restSeconds
   374‚Üí
   375‚Üí        // Calculate completion percentage
   376‚Üí        let completionPercentage = Int(overallProgress * 100)
   377‚Üí
   378‚Üí        // Update Live Activity
   379‚Üí        await manager.updateActivity(
   380‚Üí            exerciseName: currentExerciseName,
   381‚Üí            series: seriesInfo,
   382‚Üí            restSeconds: restSeconds,
   383‚Üí            totalTime: formattedWorkoutTime,
   384‚Üí            canGoNext: isCurrentExerciseComplete && !isResting,
   385‚Üí            completionPercentage: completionPercentage,
   386‚Üí            workoutState: workoutState
   387‚Üí        )
   388‚Üí    }
   389‚Üí
   390‚Üí    // MARK: - Workout Completion
   391‚Üí
   392‚Üí    /// Finishes workout with given status
   393‚Üí    func finishWorkout(status: WorkoutStatus) async {
   394‚Üí        do {
   395‚Üí            workoutTimer.pause()
   396‚Üí            restTimer.stop()
   397‚Üí            stopLiveActivityUpdateTimer()
   398‚Üí
   399‚Üí            // End Live Activity if available
   400‚Üí            if #available(iOS 16.1, *) {
   401‚Üí                await liveActivityManager?.endActivity()
   402‚Üí            }
   403‚Üí
   404‚Üí            try await sessionStore.finish(status: status)
   405‚Üí            showCompletionScreen = true
   406‚Üí        } catch {
   407‚Üí            errorMessage = error.localizedDescription
   408‚Üí        }
   409‚Üí    }
   410‚Üí
   411‚Üí    /// Dismisses completion screen and resets workout
   412‚Üí    func dismissCompletion() {
   413‚Üí        showCompletionScreen = false
   414‚Üí        sessionStore.reset()
   415‚Üí        workoutTimer.reset()
   416‚Üí        restTimer.stop()
   417‚Üí        stopLiveActivityUpdateTimer()
   418‚Üí
   419‚Üí        // Ensure Live Activity is ended
   420‚Üí        if #available(iOS 16.1, *) {
   421‚Üí            Task {
   422‚Üí                await liveActivityManager?.endActivity()
   423‚Üí            }
   424‚Üí        }
   425‚Üí    }
   426‚Üí
   427‚Üí    // MARK: - Error Handling
   428‚Üí
   429‚Üí    /// Clears error message
   430‚Üí    func clearError() {
   431‚Üí        errorMessage = nil
   432‚Üí    }
   433‚Üí}
