    10‚Üí
    11‚Üí/// ViewModel for building and editing custom workout templates
    12‚Üí@Observable
    13‚Üí@MainActor
    14‚Üífinal class CustomWorkoutBuilderViewModel {
    15‚Üí    // MARK: - State
    16‚Üí
    17‚Üí    var name: String = ""
    18‚Üí    var exercises: [CustomExerciseEntry] = []
    19‚Üí    var category: String?
    20‚Üí
    21‚Üí    var isLoading = false
    22‚Üí    var error: Error?
    23‚Üí    var showExercisePicker = false
    24‚Üí    var showSaveConfirmation = false
    25‚Üí
    26‚Üí    // MARK: - Dependencies
    27‚Üí
    28‚Üí    private let saveUseCase: SaveCustomWorkoutUseCase
    29‚Üí    private let existingTemplate: CustomWorkoutTemplate?
    30‚Üí    let exerciseService: (any ExerciseServiceProtocol)?
    31‚Üí
    32‚Üí    // MARK: - Computed Properties
    33‚Üí
    34‚Üí    var canSave: Bool {
    35‚Üí        !name.trimmingCharacters(in: .whitespacesAndNewlines).isEmpty && !exercises.isEmpty
    36‚Üí    }
    37‚Üí
    38‚Üí    var estimatedDuration: Int {
    39‚Üí        exercises.reduce(0) { $0 + $1.estimatedDurationMinutes }
    40‚Üí    }
    41‚Üí
    42‚Üí    var totalSets: Int {
    43‚Üí        exercises.reduce(0) { $0 + $1.sets.count }
    44‚Üí    }
    45‚Üí
    46‚Üí    var isEditing: Bool {
    47‚Üí        existingTemplate != nil
    48‚Üí    }
    49‚Üí
    50‚Üí    // MARK: - Initialization
    51‚Üí
    52‚Üí    init(
    53‚Üí        saveUseCase: SaveCustomWorkoutUseCase,
    54‚Üí        existingTemplate: CustomWorkoutTemplate? = nil,
    55‚Üí        existingTemplateId: UUID? = nil,
    56‚Üí        exerciseService: (any ExerciseServiceProtocol)? = nil
    57‚Üí    ) {
    58‚Üí        self.saveUseCase = saveUseCase
    59‚Üí        self.existingTemplate = existingTemplate
    60‚Üí        self.exerciseService = exerciseService
    61‚Üí
    62‚Üí        // Populate from existing template if editing
    63‚Üí        if let template = existingTemplate {
    64‚Üí            self.name = template.name
    65‚Üí            self.exercises = template.exercises
    66‚Üí            self.category = template.category
    67‚Üí        }
    68‚Üí        // Note: existingTemplateId would be used to load template async if needed
    69‚Üí    }
    70‚Üí
    71‚Üí    // MARK: - Actions
    72‚Üí
    73‚Üí    func addExercise(from exercise: WgerExercise, imageURL: String? = nil) {
    74‚Üí        let entry = CustomExerciseEntry(from: exercise, orderIndex: exercises.count, imageURL: imageURL)
    75‚Üí        exercises.append(entry)
    76‚Üí    }
    77‚Üí
    78‚Üí    func addExercise(_ entry: CustomExerciseEntry) {
    79‚Üí        var newEntry = entry
    80‚Üí        newEntry.orderIndex = exercises.count
    81‚Üí        exercises.append(newEntry)
    82‚Üí    }
    83‚Üí
    84‚Üí    func removeExercise(at index: Int) {
    85‚Üí        guard exercises.indices.contains(index) else { return }
    86‚Üí        exercises.remove(at: index)
    87‚Üí        reorderExercises()
    88‚Üí    }
    89‚Üí
    90‚Üí    func moveExercise(from source: IndexSet, to destination: Int) {
    91‚Üí        exercises.move(fromOffsets: source, toOffset: destination)
    92‚Üí        reorderExercises()
    93‚Üí    }
    94‚Üí
    95‚Üí    func addSet(to exerciseIndex: Int) {
    96‚Üí        guard exercises.indices.contains(exerciseIndex) else { return }
    97‚Üí
    98‚Üí        // Copy last set's target values if available
    99‚Üí        let newSet: WorkoutSet
   100‚Üí        if let lastSet = exercises[exerciseIndex].sets.last {
   101‚Üí            newSet = WorkoutSet(
   102‚Üí                targetReps: lastSet.targetReps,
   103‚Üí                targetWeight: lastSet.targetWeight,
   104‚Üí                targetDuration: lastSet.targetDuration
   105‚Üí            )
   106‚Üí        } else {
   107‚Üí            newSet = WorkoutSet()
   108‚Üí        }
   109‚Üí
   110‚Üí        exercises[exerciseIndex].sets.append(newSet)
   111‚Üí    }
   112‚Üí
   113‚Üí    func removeSet(from exerciseIndex: Int, at setIndex: Int) {
   114‚Üí        guard exercises.indices.contains(exerciseIndex) else { return }
   115‚Üí        guard exercises[exerciseIndex].sets.indices.contains(setIndex) else { return }
   116‚Üí
   117‚Üí        // Don't allow removing the last set
   118‚Üí        guard exercises[exerciseIndex].sets.count > 1 else { return }
   119‚Üí
   120‚Üí        exercises[exerciseIndex].sets.remove(at: setIndex)
   121‚Üí    }
   122‚Üí
   123‚Üí    func updateSet(exerciseIndex: Int, setIndex: Int, reps: Int?, weight: Double?) {
   124‚Üí        guard exercises.indices.contains(exerciseIndex) else { return }
   125‚Üí        guard exercises[exerciseIndex].sets.indices.contains(setIndex) else { return }
   126‚Üí
   127‚Üí        exercises[exerciseIndex].sets[setIndex].targetReps = reps
   128‚Üí        exercises[exerciseIndex].sets[setIndex].targetWeight = weight
   129‚Üí    }
   130‚Üí
   131‚Üí    func updateNotes(for exerciseIndex: Int, notes: String?) {
   132‚Üí        guard exercises.indices.contains(exerciseIndex) else { return }
   133‚Üí        exercises[exerciseIndex].notes = notes?.isEmpty == true ? nil : notes
   134‚Üí    }
   135‚Üí
   136‚Üí    func save() async throws -> CustomWorkoutTemplate {
   137‚Üí        isLoading = true
   138‚Üí        error = nil
   139‚Üí
   140‚Üí        defer { isLoading = false }
   141‚Üí
   142‚Üí        let template = CustomWorkoutTemplate(
   143‚Üí            id: existingTemplate?.id ?? UUID(),
   144‚Üí            name: name.trimmingCharacters(in: .whitespacesAndNewlines),
   145‚Üí            exercises: exercises,
   146‚Üí            createdAt: existingTemplate?.createdAt ?? Date(),
   147‚Üí            lastUsedAt: existingTemplate?.lastUsedAt,
   148‚Üí            category: category
   149‚Üí        )
   150‚Üí
   151‚Üí        do {
   152‚Üí            try await saveUseCase.execute(template: template)
   153‚Üí            showSaveConfirmation = true
   154‚Üí            return template
   155‚Üí        } catch {
   156‚Üí            self.error = error
   157‚Üí            throw error
   158‚Üí        }
   159‚Üí    }
   160‚Üí
   161‚Üí    func reset() {
   162‚Üí        name = ""
   163‚Üí        exercises = []
   164‚Üí        category = nil
   165‚Üí        error = nil
   166‚Üí    }
   167‚Üí
   168‚Üí    // MARK: - Private
   169‚Üí
   170‚Üí    private func reorderExercises() {
   171‚Üí        for i in exercises.indices {
   172‚Üí            exercises[i].orderIndex = i
   173‚Üí        }
   174‚Üí    }
   175‚Üí}
