   214‚Üí// MARK: - ViewModel
   215‚Üí
   216‚Üí@MainActor
   217‚Üí@Observable final class ProgramsListViewModel {
   218‚Üí    private(set) var programs: [Program] = []
   219‚Üí    private(set) var isLoading = false
   220‚Üí    var errorMessage: String?
   221‚Üí
   222‚Üí    private let repository: ProgramRepository
   223‚Üí
   224‚Üí    init(repository: ProgramRepository) {
   225‚Üí        self.repository = repository
   226‚Üí    }
   227‚Üí
   228‚Üí    // MARK: - Computed Properties
   229‚Üí
   230‚Üí    var beginnerCount: Int {
   231‚Üí        programs.filter { $0.level == .beginner }.count
   232‚Üí    }
   233‚Üí
   234‚Üí    var intermediateCount: Int {
   235‚Üí        programs.filter { $0.level == .intermediate }.count
   236‚Üí    }
   237‚Üí
   238‚Üí    var advancedCount: Int {
   239‚Üí        programs.filter { $0.level == .advanced }.count
   240‚Üí    }
   241‚Üí
   242‚Üí    struct ProgramGroup {
   243‚Üí        let category: ProgramCategory
   244‚Üí        let programs: [Program]
   245‚Üí    }
   246‚Üí
   247‚Üí    var groupedPrograms: [ProgramGroup] {
   248‚Üí        let grouped = Dictionary(grouping: programs) { $0.category }
   249‚Üí
   250‚Üí        return ProgramCategory.allCases
   251‚Üí            .compactMap { category -> ProgramGroup? in
   252‚Üí                guard let programs = grouped[category], !programs.isEmpty else {
   253‚Üí                    return nil
   254‚Üí                }
   255‚Üí                // Sort programs within category by level
   256‚Üí                let sortedPrograms = programs.sorted { p1, p2 in
   257‚Üí                    let levelOrder: [ProgramLevel: Int] = [.beginner: 0, .intermediate: 1, .advanced: 2]
   258‚Üí                    return (levelOrder[p1.level] ?? 0) < (levelOrder[p2.level] ?? 0)
   259‚Üí                }
   260‚Üí                return ProgramGroup(category: category, programs: sortedPrograms)
   261‚Üí            }
   262‚Üí            .sorted { $0.category.sortOrder < $1.category.sortOrder }
   263‚Üí    }
   264‚Üí
   265‚Üí    // MARK: - Methods
   266‚Üí
   267‚Üí    func loadPrograms() async {
   268‚Üí        isLoading = true
   269‚Üí        defer { isLoading = false }
   270‚Üí
   271‚Üí        do {
   272‚Üí            programs = try await repository.listPrograms()
   273‚Üí            #if DEBUG
   274‚Üí            print("[ProgramsListViewModel] Loaded \(programs.count) programs")
   275‚Üí            for category in ProgramCategory.allCases {
   276‚Üí                let count = programs.filter { $0.category == category }.count
   277‚Üí                if count > 0 {
