   292‚Üí
   293‚Üí#Preview {
   294‚Üí    let container = Container()
   295‚Üí    container.register(ProgramRepository.self) { _ in BundleProgramRepository() }
   296‚Üí
   297‚Üí    return NavigationStack {
   298‚Üí        ProgramsListView()
   299‚Üí            .environment(AppRouter())
   300‚Üí            .environment(\.dependencyResolver, container)
   301‚Üí    }
   302‚Üí}
