   243‚Üí    private var profile: UserProfile?
   244‚Üí    func loadProfile() async throws -> UserProfile? { profile }
   245‚Üí    func saveProfile(_ profile: UserProfile) async throws { self.profile = profile }
   246‚Üí}
   247‚Üí
   248‚Üíprivate final class InMemoryEntitlementRepository: EntitlementRepository, @unchecked Sendable {
   249‚Üí    func currentEntitlement() async throws -> ProEntitlement { .free }
   250‚Üí    func entitlementStream() -> AsyncStream<ProEntitlement> {
   251‚Üí        AsyncStream { continuation in continuation.finish() }
   252‚Üí    }
   253‚Üí}
   254‚Üí
   255‚Üíprivate final class InMemoryHistoryRepository: WorkoutHistoryRepository {
   256‚Üí    private var entries: [WorkoutHistoryEntry] = []
   257‚Üí
   258‚Üí    func listEntries() async throws -> [WorkoutHistoryEntry] {
   259‚Üí        entries.sorted { $0.date > $1.date }
   260‚Üí    }
   261‚Üí    
   262‚Üí    func listEntries(limit: Int, offset: Int) async throws -> [WorkoutHistoryEntry] {
   263‚Üí        let sorted = entries.sorted { $0.date > $1.date }
   264‚Üí        let start = min(offset, sorted.count)
   265‚Üí        let end = min(offset + limit, sorted.count)
   266‚Üí        return Array(sorted[start..<end])
   267‚Üí    }
   268‚Üí    
   269‚Üí    func count() async throws -> Int {
   270‚Üí        entries.count
   271‚Üí    }
   272‚Üí
   273‚Üí    func saveEntry(_ entry: WorkoutHistoryEntry) async throws {
   274‚Üí        entries.append(entry)
   275‚Üí    }
   276‚Üí
   277‚Üí    func listAppEntriesWithPlan(limit: Int) async throws -> [WorkoutHistoryEntry] {
   278‚Üí        let filtered = entries
   279‚Üí            .filter { $0.source == .app && $0.workoutPlan != nil }
   280‚Üí            .sorted { $0.date > $1.date }
   281‚Üí        return Array(filtered.prefix(limit))
   282‚Üí    }
   283‚Üí}
