   193‚Üí    private func filteredPrograms(_ programs: [Program]) -> [Program] {
   194‚Üí        guard selectedFilter != .all else { return programs }
   195‚Üí        return programs.filter { filterForProgram($0) == selectedFilter }
   196‚Üí    }
   197‚Üí
   198‚Üí    private func filterForProgram(_ program: Program) -> ProgramFilter {
   199‚Üí        // Map program goal to filter
   200‚Üí        switch program.goalTag {
   201‚Üí        case .strength:
   202‚Üí            return .strength
   203‚Üí        case .conditioning:
   204‚Üí            return .conditioning
   205‚Üí        case .aerobic:
   206‚Üí            return .aerobic
   207‚Üí        case .core:
   208‚Üí            return .wellness
   209‚Üí        case .endurance:
   210‚Üí            return .endurance
   211‚Üí        }
   212‚Üí    }
   213‚Üí}
