//
//  CMSWorkoutService.swift
//  FitToday
//
//  Actor-based service for CMS Personal Trainer API integration.
//  Handles all REST API interactions for trainer workouts.
//

import Foundation

// MARK: - CMS Configuration

/// Configuration for CMS API connection.
struct CMSConfiguration: Sendable {
    let baseURL: URL
    let apiKey: String?
    let timeout: TimeInterval

    static let `default` = CMSConfiguration(
        baseURL: URL(string: "https://api.fittoday.app")!,
        apiKey: nil,
        timeout: 30
    )

    init(baseURL: URL, apiKey: String? = nil, timeout: TimeInterval = 30) {
        self.baseURL = baseURL
        self.apiKey = apiKey
        self.timeout = timeout
    }
}

// MARK: - CMS Workout Service

/// Actor-based service for CMS workout API operations.
///
/// Provides type-safe access to all CMS workout endpoints:
/// - GET/POST /api/workouts
/// - GET/PATCH/DELETE /api/workouts/[id]
/// - GET /api/workouts/[id]/progress
/// - GET/POST /api/workouts/[id]/feedback
actor CMSWorkoutService {

    // MARK: - Properties

    private let session: URLSession
    private let configuration: CMSConfiguration
    private let decoder: JSONDecoder
    private let encoder: JSONEncoder

    // MARK: - Initialization

    init(configuration: CMSConfiguration = .default) {
        self.configuration = configuration

        let sessionConfig = URLSessionConfiguration.default
        sessionConfig.timeoutIntervalForRequest = configuration.timeout
        sessionConfig.timeoutIntervalForResource = configuration.timeout * 2
        self.session = URLSession(configuration: sessionConfig)

        self.decoder = JSONDecoder()
        self.decoder.dateDecodingStrategy = .iso8601

        self.encoder = JSONEncoder()
        self.encoder.dateEncodingStrategy = .iso8601
    }

    // MARK: - Workouts API

    /// Fetches workouts for a student from the CMS.
    ///
    /// - Parameters:
    ///   - studentId: The student's user ID.
    ///   - trainerId: Optional trainer ID to filter by.
    ///   - page: Page number for pagination (default: 1).
    ///   - limit: Number of items per page (default: 20).
    /// - Returns: A paginated list of workouts.
    /// - Throws: CMSServiceError if the request fails.
    func fetchWorkouts(
        studentId: String,
        trainerId: String? = nil,
        page: Int = 1,
        limit: Int = 20
    ) async throws -> CMSWorkoutListResponse {
        var components = URLComponents(url: configuration.baseURL.appendingPathComponent("/api/workouts"), resolvingAgainstBaseURL: true)!
        components.queryItems = [
            URLQueryItem(name: "student_id", value: studentId),
            URLQueryItem(name: "page", value: String(page)),
            URLQueryItem(name: "limit", value: String(limit))
        ]

        if let trainerId {
            components.queryItems?.append(URLQueryItem(name: "trainer_id", value: trainerId))
        }

        guard let url = components.url else {
            throw CMSServiceError.invalidURL
        }

        let request = try buildRequest(url: url, method: "GET")
        return try await execute(request: request)
    }

    /// Fetches a single workout by ID.
    ///
    /// - Parameter id: The workout ID.
    /// - Returns: The workout details.
    /// - Throws: CMSServiceError if the request fails.
    func fetchWorkout(id: String) async throws -> CMSWorkout {
        let url = configuration.baseURL.appendingPathComponent("/api/workouts/\(id)")
        let request = try buildRequest(url: url, method: "GET")
        return try await execute(request: request)
    }

    /// Updates a workout's status or metadata.
    ///
    /// - Parameters:
    ///   - id: The workout ID.
    ///   - update: The update request body.
    /// - Returns: The updated workout.
    /// - Throws: CMSServiceError if the request fails.
    func updateWorkout(id: String, update: CMSWorkoutUpdateRequest) async throws -> CMSWorkout {
        let url = configuration.baseURL.appendingPathComponent("/api/workouts/\(id)")
        var request = try buildRequest(url: url, method: "PATCH")
        request.httpBody = try encoder.encode(update)
        return try await execute(request: request)
    }

    /// Marks a workout as deleted (soft delete).
    ///
    /// - Parameter id: The workout ID.
    /// - Throws: CMSServiceError if the request fails.
    func deleteWorkout(id: String) async throws {
        let url = configuration.baseURL.appendingPathComponent("/api/workouts/\(id)")
        let request = try buildRequest(url: url, method: "DELETE")
        let _: EmptyResponse = try await execute(request: request)
    }

    // MARK: - Progress API

    /// Fetches the student's progress for a workout.
    ///
    /// - Parameter workoutId: The workout ID.
    /// - Returns: The progress data.
    /// - Throws: CMSServiceError if the request fails.
    func fetchProgress(workoutId: String) async throws -> CMSWorkoutProgress {
        let url = configuration.baseURL.appendingPathComponent("/api/workouts/\(workoutId)/progress")
        let request = try buildRequest(url: url, method: "GET")
        return try await execute(request: request)
    }

    // MARK: - Feedback API

    /// Fetches all feedback for a workout.
    ///
    /// - Parameter workoutId: The workout ID.
    /// - Returns: An array of feedback items.
    /// - Throws: CMSServiceError if the request fails.
    func fetchFeedback(workoutId: String) async throws -> [CMSWorkoutFeedback] {
        let url = configuration.baseURL.appendingPathComponent("/api/workouts/\(workoutId)/feedback")
        let request = try buildRequest(url: url, method: "GET")
        return try await execute(request: request)
    }

    /// Posts new feedback for a workout.
    ///
    /// - Parameters:
    ///   - workoutId: The workout ID.
    ///   - feedback: The feedback request body.
    /// - Returns: The created feedback.
    /// - Throws: CMSServiceError if the request fails.
    func postFeedback(workoutId: String, feedback: CMSFeedbackRequest) async throws -> CMSWorkoutFeedback {
        let url = configuration.baseURL.appendingPathComponent("/api/workouts/\(workoutId)/feedback")
        var request = try buildRequest(url: url, method: "POST")
        request.httpBody = try encoder.encode(feedback)
        return try await execute(request: request)
    }

    // MARK: - Private Helpers

    private func buildRequest(url: URL, method: String) throws -> URLRequest {
        var request = URLRequest(url: url)
        request.httpMethod = method
        request.setValue("application/json", forHTTPHeaderField: "Content-Type")
        request.setValue("application/json", forHTTPHeaderField: "Accept")

        if let apiKey = configuration.apiKey {
            request.setValue("Bearer \(apiKey)", forHTTPHeaderField: "Authorization")
        }

        return request
    }

    private func execute<T: Decodable>(request: URLRequest) async throws -> T {
        #if DEBUG
        print("[CMSWorkoutService] \(request.httpMethod ?? "GET") \(request.url?.absoluteString ?? "")")
        #endif

        let (data, response) = try await session.data(for: request)

        guard let httpResponse = response as? HTTPURLResponse else {
            throw CMSServiceError.invalidResponse
        }

        #if DEBUG
        print("[CMSWorkoutService] Status: \(httpResponse.statusCode)")
        #endif

        switch httpResponse.statusCode {
        case 200...299:
            do {
                return try decoder.decode(T.self, from: data)
            } catch {
                #if DEBUG
                print("[CMSWorkoutService] Decode error: \(error)")
                if let json = String(data: data, encoding: .utf8) {
                    print("[CMSWorkoutService] Response: \(json.prefix(500))")
                }
                #endif
                throw CMSServiceError.decodingFailed(error)
            }

        case 401:
            throw CMSServiceError.unauthorized

        case 403:
            throw CMSServiceError.forbidden

        case 404:
            throw CMSServiceError.notFound

        case 429:
            throw CMSServiceError.rateLimited

        case 500...599:
            throw CMSServiceError.serverError(httpResponse.statusCode)

        default:
            if let apiError = try? decoder.decode(CMSAPIError.self, from: data) {
                throw CMSServiceError.apiError(apiError)
            }
            throw CMSServiceError.unexpectedStatus(httpResponse.statusCode)
        }
    }
}

// MARK: - Empty Response

/// Used for endpoints that return no body on success.
private struct EmptyResponse: Decodable {}

// MARK: - CMS Service Error

/// Errors that can occur during CMS API operations.
enum CMSServiceError: LocalizedError, Sendable {
    case invalidURL
    case invalidResponse
    case unauthorized
    case forbidden
    case notFound
    case rateLimited
    case serverError(Int)
    case unexpectedStatus(Int)
    case decodingFailed(Error)
    case apiError(CMSAPIError)
    case networkError(Error)

    var errorDescription: String? {
        switch self {
        case .invalidURL:
            return "URL invalida"
        case .invalidResponse:
            return "Resposta invalida do servidor"
        case .unauthorized:
            return "Sessao expirada. Faca login novamente."
        case .forbidden:
            return "Voce nao tem permissao para acessar este recurso"
        case .notFound:
            return "Treino nao encontrado"
        case .rateLimited:
            return "Muitas requisicoes. Aguarde um momento."
        case .serverError(let code):
            return "Erro no servidor (\(code)). Tente novamente."
        case .unexpectedStatus(let code):
            return "Erro inesperado (\(code))"
        case .decodingFailed:
            return "Erro ao processar resposta"
        case .apiError(let error):
            return error.message
        case .networkError:
            return "Erro de conexao. Verifique sua internet."
        }
    }
}

//
//  CMSWorkoutModels.swift
//  FitToday
//
//  DTOs for CMS Personal Trainer API integration.
//  Supports endpoints: /api/workouts, /api/workouts/[id], progress, feedback
//

import Foundation

// MARK: - CMS Workout Response

/// Response wrapper for paginated workout lists from CMS API.
struct CMSWorkoutListResponse: Codable, Sendable {
    let workouts: [CMSWorkout]
    let total: Int
    let page: Int
    let limit: Int
    let hasMore: Bool

    enum CodingKeys: String, CodingKey {
        case workouts
        case total
        case page
        case limit
        case hasMore = "has_more"
    }
}

// MARK: - CMS Workout DTO

/// DTO for workout data from CMS API.
/// Matches: GET /api/workouts and GET /api/workouts/[id]
struct CMSWorkout: Codable, Sendable, Identifiable {
    let id: String
    let trainerId: String
    let studentId: String
    let title: String
    let description: String?
    let focus: String
    let estimatedDurationMinutes: Int
    let intensity: String
    let phases: [CMSWorkoutPhase]
    let schedule: CMSWorkoutSchedule?
    let status: CMSWorkoutStatus
    let pdfUrl: String?
    let version: Int
    let createdAt: Date
    let updatedAt: Date

    enum CodingKeys: String, CodingKey {
        case id
        case trainerId = "trainer_id"
        case studentId = "student_id"
        case title
        case description
        case focus
        case estimatedDurationMinutes = "estimated_duration_minutes"
        case intensity
        case phases
        case schedule
        case status
        case pdfUrl = "pdf_url"
        case version
        case createdAt = "created_at"
        case updatedAt = "updated_at"
    }
}

// MARK: - CMS Workout Phase

/// Phase/section within a CMS workout.
struct CMSWorkoutPhase: Codable, Sendable {
    let name: String
    let order: Int
    let items: [CMSWorkoutItem]
}

// MARK: - CMS Workout Item

/// Individual exercise within a workout phase.
struct CMSWorkoutItem: Codable, Sendable {
    let id: String
    let exerciseId: Int?
    let exerciseName: String
    let sets: Int
    let reps: String
    let weight: String?
    let restSeconds: Int
    let notes: String?
    let order: Int
    let mediaUrl: String?

    enum CodingKeys: String, CodingKey {
        case id
        case exerciseId = "exercise_id"
        case exerciseName = "exercise_name"
        case sets
        case reps
        case weight
        case restSeconds = "rest_seconds"
        case notes
        case order
        case mediaUrl = "media_url"
    }
}

// MARK: - CMS Workout Schedule

/// Schedule configuration for CMS workouts.
struct CMSWorkoutSchedule: Codable, Sendable {
    let type: String
    let scheduledDate: Date?
    let dayOfWeek: Int?
    let recurrence: String?

    enum CodingKeys: String, CodingKey {
        case type
        case scheduledDate = "scheduled_date"
        case dayOfWeek = "day_of_week"
        case recurrence
    }
}

// MARK: - CMS Workout Status

/// Status of a workout in the CMS system.
enum CMSWorkoutStatus: String, Codable, Sendable {
    case draft
    case active
    case completed
    case archived
    case deleted
}

// MARK: - CMS Workout Progress

/// Progress data for a workout.
/// Matches: GET /api/workouts/[id]/progress
struct CMSWorkoutProgress: Codable, Sendable {
    let workoutId: String
    let studentId: String
    let completedSessions: Int
    let totalSessions: Int
    let lastSessionDate: Date?
    let exerciseProgress: [CMSExerciseProgress]
    let overallProgress: Double

    enum CodingKeys: String, CodingKey {
        case workoutId = "workout_id"
        case studentId = "student_id"
        case completedSessions = "completed_sessions"
        case totalSessions = "total_sessions"
        case lastSessionDate = "last_session_date"
        case exerciseProgress = "exercise_progress"
        case overallProgress = "overall_progress"
    }
}

// MARK: - CMS Exercise Progress

/// Progress for an individual exercise.
struct CMSExerciseProgress: Codable, Sendable {
    let exerciseId: String
    let exerciseName: String
    let completedSets: Int
    let targetSets: Int
    let maxWeight: Double?
    let notes: String?

    enum CodingKeys: String, CodingKey {
        case exerciseId = "exercise_id"
        case exerciseName = "exercise_name"
        case completedSets = "completed_sets"
        case targetSets = "target_sets"
        case maxWeight = "max_weight"
        case notes
    }
}

// MARK: - CMS Feedback

/// Feedback data for a workout.
/// Matches: GET/POST /api/workouts/[id]/feedback
struct CMSWorkoutFeedback: Codable, Sendable, Identifiable {
    let id: String
    let workoutId: String
    let studentId: String
    let trainerId: String?
    let type: CMSFeedbackType
    let message: String
    let rating: Int?
    let createdAt: Date
    let repliedAt: Date?
    let replyMessage: String?

    enum CodingKeys: String, CodingKey {
        case id
        case workoutId = "workout_id"
        case studentId = "student_id"
        case trainerId = "trainer_id"
        case type
        case message
        case rating
        case createdAt = "created_at"
        case repliedAt = "replied_at"
        case replyMessage = "reply_message"
    }
}

// MARK: - CMS Feedback Type

/// Type of feedback in the CMS system.
enum CMSFeedbackType: String, Codable, Sendable {
    case general
    case difficulty
    case exercise
    case completion
    case question
}

// MARK: - CMS Feedback Request

/// Request body for posting feedback.
/// Matches: POST /api/workouts/[id]/feedback
struct CMSFeedbackRequest: Codable, Sendable {
    let type: CMSFeedbackType
    let message: String
    let rating: Int?

    init(type: CMSFeedbackType, message: String, rating: Int? = nil) {
        self.type = type
        self.message = message
        self.rating = rating
    }
}

// MARK: - CMS Workout Update Request

/// Request body for updating a workout.
/// Matches: PATCH /api/workouts/[id]
struct CMSWorkoutUpdateRequest: Codable, Sendable {
    let status: CMSWorkoutStatus?
    let title: String?
    let description: String?

    init(status: CMSWorkoutStatus? = nil, title: String? = nil, description: String? = nil) {
        self.status = status
        self.title = title
        self.description = description
    }
}

// MARK: - CMS API Error Response

/// Error response from CMS API.
struct CMSAPIError: Codable, Sendable, LocalizedError {
    let code: String
    let message: String
    let details: String?

    var errorDescription: String? { message }
}

