    // MARK: - Actions

    private func actionsSection(_ workout: TrainerWorkout) -> some View {
        VStack(spacing: FitTodaySpacing.md) {
            // Feedback button
            Button {
                router.push(.cmsWorkoutFeedback(workout.id), on: .workout)
            } label: {
                HStack {
